#Find the largest/smallest number in a list/tupledef list1():    L1=int(input('enter no. of elements in list: '))    L2=[]    for i in range(L1):        L1_element=int(input('enter element: '))        L2.append(L1_element)    print(L2)    L2_len=len(L2)    L2_greater=L2[0]    for j in range(L2_len):        if L2[j]>L2_greater:            L2_greater=L2[j]    print('largest number is: ',L2_greater)list1()        