#Input three numbers and display the largest / smallest number. def max_min():    a=int(input('enter first number: '))    b=int(input('enter second number: '))    c=int(input('enter third number: '))    if a>b:        if a>c:            print('Largest number is: ',a)            print('smallest number is: ',c)        else:            print('Largest number is: ',c)            print('smallest number is: ',a)    elif b>c:        if a>c:            print('Largest number is: ',b)            print('smallest number is: ',c)        else:           print('Largest number is: ',b)           print('smallest number is: ',a)    else:        print('Largest number is: ',c)        print('smallest number is: ',a)max_min()                