#Determine whether a number is a perfect number, an armstrong number or a palindrome.num=int(input('enter number: '))def perfectnos(num):    num1=num//2    fact=[]    for i in range(1,num1+1):                                   if num%i==0:                                                       fact.append(i)        nos=len(fact)    sum1=0    for j in range(nos):        sum1+=fact[j]    if num==sum1:        print(num,'is a perfect nos.')    else:        print(num,'is not a perfect nos.')def palindrome(num):    rev=0    num1=num    while num>0:        rev=rev*10+(num%10)        num=num//10    if num1==rev:        print(num1,'is a palindrome nos')    else:        print(num1,'is a palindrome nos')def armstrong(num):    check=0    num1=num    while num>0:        check+=(num%10)**3        num=num//10    if num1==check:        print(num1,"is a armstrong no. ")    else:        print(num1,"is not a armstrong no. ")perfectnos(num)palindrome(num)armstrong(num)